// Modern JavaScript

(() => {
    "use strict";
    const scrollButton = document.querySelector(".scroll-to-top");
    if (scrollButton) {
      window.addEventListener("scroll", () => {
        const yOffset = window.pageYOffset;
        scrollButton.style.display = yOffset > 100 ? "block" : "none";
      });
    }
    
    const mainNav = document.querySelector("#mainNav");
    if (mainNav) {
      const navbarCollapse = mainNav.querySelector(".navbar-collapse");
      if (navbarCollapse) {
        const collapse = new bootstrap.Collapse(navbarCollapse, { toggle: false });
        const links = navbarCollapse.querySelectorAll("a");
        links.forEach(link => {
          link.addEventListener("click", () => {
            collapse.hide();
          });
        });
      }
      const handleScroll = () => {
        const yOffset = window.pageYOffset || (
          document.documentElement ||
          document.body.parentNode ||
          document.body
        ).scrollTop;
        yOffset > 100 ? mainNav.classList.add("navbar-shrink") : mainNav.classList.remove("navbar-shrink");
      };
      handleScroll();
      document.addEventListener("scroll", handleScroll);
    }
  })();
  